-- ============================================
-- HR Evaluation System - Import Data
-- Import Data from MySQL to Supabase
-- Run this AFTER running schema.sql
-- ============================================

-- ============================================
-- Import Users (51 records)
-- ============================================
ON CONFLICT (internal_id) DO UPDATE SET
  name = EXCLUDED.name,
  position = EXCLUDED.position,
  dept = EXCLUDED.dept,
  mobile = EXCLUDED.mobile,
  salary = EXCLUDED.salary,
  salary_group = EXCLUDED.salary_group,
  role = EXCLUDED.role,
  parent_internal_id = EXCLUDED.parent_internal_id,
  img = EXCLUDED.img,
  is_admin = EXCLUDED.is_admin,
  can_view_report = EXCLUDED.can_view_report,
  is_active = EXCLUDED.is_active,
  email = EXCLUDED.email,
  permissions = EXCLUDED.permissions;

-- ============================================
-- Import Criteria (27 records)
-- ============================================
ON CONFLICT (id) DO UPDATE SET
  text = EXCLUDED.text,
  category = EXCLUDED.category,
  weight = EXCLUDED.weight,
  description = EXCLUDED.description;

-- ============================================
-- Import Exclusions (13 records)
-- ============================================
ON CONFLICT DO NOTHING;

-- ============================================
-- Import System Config (10 records)
-- ============================================
ON CONFLICT (key) DO UPDATE SET value = EXCLUDED.value;

-- ============================================
-- Success Message
-- ============================================
DO $$
BEGIN
  RAISE NOTICE 'Data import completed successfully!';
  RAISE NOTICE 'Imported: 51 users, 27 criteria, 13 exclusions, 10 system configs';
END $$;

